import {
  Box,
  Button,
  TextField,
  useMediaQuery,
  Typography,
  useTheme,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import Myimg from "../../asset/twitterLoginLogo.jpg";




const Xpagebuttons= () => {
  const { palette } = useTheme();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const isNonMobile = useMediaQuery("(min-width:600px)");
  return (
  <Box p = "15rem" >
      <Typography
        sx={{
            color : "white"
        }}
        variant="h1"
        gutterBottom = "50"
      >
        正發生
      </Typography>

      <Typography
        sx={{
            color : "white"
        }}
        variant="h1"
      >
         立即加入
      </Typography>

      <Button
        fullWidth
        type="button"
        sx={{
            m: "2rem 0",
            p: "1rem",
            color:"black",
            backgroundColor: "white",
            "&:hover": { backgroundColor:"white"},
            
        }}
      >
        使用Goolge帳戶註冊
      </Button>

      <Button
        fullWidth
        type="button"
        sx={{
            m: "2rem 0",
            p: "1rem",
            backgroundColor: "white",
            color:"black",
            "&:hover": { backgroundColor:"white"},
        }}
      >
        使用Apple帳戶註冊
      </Button>

      <Typography
        sx={{
            color : "gray"
        }}
      >
        <hr/>
      </Typography>

      <Button
        fullWidth
        type="button"
        sx={{
            m: "2rem 0",
            p: "1rem",
            backgroundColor: "palegreen",
            color:"black",
            "&:hover": { backgroundColor:"palegreen"},
        }}
      >
        建立帳戶
      </Button>
      
      <Typography
        sx={{
        color : "white"
        }}
        variant="h5"
        gutterBottom = "50"
      >
        已經有帳戶了嗎?
      </Typography>
      
      <Button

        onClick={()=>{navigate("/Login")}}
        fullWidth
        type="button"
        sx={{
            m: "2rem 0",
            p: "1rem",
            backgroundColor: "palegreen",
            color:"black",
            "&:hover": { backgroundColor:"palegreen"},
        }}
      >
        <img src = {Myimg} alt= "myimg" width = "3%"height="3%" />
        <div>
            登入
        </div>
      </Button>
  </Box>
  )}

export default Xpagebuttons ;
